import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext } from '../contexts/ThemeContext';

function Navbar() {
  const { theme, toggleTheme } = useContext(ThemeContext);

  return (
    <nav
      style={{
        backgroundColor: theme === 'light' ? '#fff' : '#333',
        color: theme === 'light' ? '#000' : '#fff',
        padding: '10px',
      }}
    >
      <ul style={{ listStyle: 'none', display: 'flex', gap: '15px', padding: 0 }}>
        <li><Link to="/" style={{ color: theme === 'light' ? '#000' : '#fff' }}>Home</Link></li>
        <li><Link to="/about" style={{ color: theme === 'light' ? '#000' : '#fff' }}>About</Link></li>
        <li><Link to="/contact" style={{ color: theme === 'light' ? '#000' : '#fff' }}>Contact</Link></li>
        <li><Link to="/redux-example" style={{ color: theme === 'light' ? '#000' : '#fff' }}>Redux Example</Link></li>
        <li><Link to="/weather-app" style={{ color: theme === 'light' ? '#000' : '#fff' }}>Weather App</Link></li>
      </ul>
      <button
        onClick={toggleTheme}
        style={{
          marginLeft: 'auto',
          padding: '5px 10px',
          backgroundColor: theme === 'light' ? '#000' : '#fff',
          color: theme === 'light' ? '#fff' : '#000',
          border: 'none',
          cursor: 'pointer',
        }}
      >
        Toggle Theme
      </button>
    </nav>
  );
}

export default Navbar;
