import React, { useContext, useState } from 'react';
import { WeatherContext } from '../context/WeatherContext';
import styles from '../styles/Weather.module.css';

const usStates = [
  'Alabama', 'Alaska', 'Arizona', 'Arkansas', 'California', 'Colorado',
  'Connecticut', 'Delaware', 'Florida', 'Georgia', 'Hawaii', 'Idaho',
  'Illinois', 'Indiana', 'Iowa', 'Kansas', 'Kentucky', 'Louisiana', 'Maine',
  'Maryland', 'Massachusetts', 'Michigan', 'Minnesota', 'Mississippi',
  'Missouri', 'Montana', 'Nebraska', 'Nevada', 'New Hampshire', 'New Jersey',
  'New Mexico', 'New York', 'North Carolina', 'North Dakota', 'Ohio',
  'Oklahoma', 'Oregon', 'Pennsylvania', 'Rhode Island', 'South Carolina',
  'South Dakota', 'Tennessee', 'Texas', 'Utah', 'Vermont', 'Virginia',
  'Washington', 'West Virginia', 'Wisconsin', 'Wyoming',
];

const stateToCityMap = {
  Alabama: 'Birmingham',
  Alaska: 'Anchorage',
  Arizona: 'Phoenix',
  Arkansas: 'Little Rock',
  California: 'Los Angeles',
  Colorado: 'Denver',
  Connecticut: 'Hartford',
  Delaware: 'Wilmington',
  Florida: 'Miami',
  Georgia: 'Atlanta',
  Hawaii: 'Honolulu',
  Idaho: 'Boise',
  Illinois: 'Chicago',
  Indiana: 'Indianapolis',
  Iowa: 'Des Moines',
  Kansas: 'Wichita',
  Kentucky: 'Louisville',
  Louisiana: 'New Orleans',
  Maine: 'Portland',
  Maryland: 'Baltimore',
  Massachusetts: 'Boston',
  Michigan: 'Detroit',
  Minnesota: 'Minneapolis',
  Mississippi: 'Jackson',
  Missouri: 'Kansas City',
  Montana: 'Billings',
  Nebraska: 'Omaha',
  Nevada: 'Las Vegas',
  'New Hampshire': 'Manchester',
  'New Jersey': 'Newark',
  'New Mexico': 'Albuquerque',
  'New York': 'New York City',
  'North Carolina': 'Charlotte',
  'North Dakota': 'Fargo',
  Ohio: 'Columbus',
  Oklahoma: 'Oklahoma City',
  Oregon: 'Portland',
  Pennsylvania: 'Philadelphia',
  'Rhode Island': 'Providence',
  'South Carolina': 'Charleston',
  'South Dakota': 'Sioux Falls',
  Tennessee: 'Nashville',
  Texas: 'Houston',
  Utah: 'Salt Lake City',
  Vermont: 'Burlington',
  Virginia: 'Richmond',
  Washington: 'Seattle',
  'West Virginia': 'Charleston',
  Wisconsin: 'Milwaukee',
  Wyoming: 'Cheyenne',
};

const WeatherApp = () => {
  const { fetchWeather, weather, loading, error } = useContext(WeatherContext);
  const [state, setState] = useState('');

  const handleSearch = () => {
    const trimmedState = state.trim();

    if (!usStates.includes(trimmedState)) {
      alert('Please enter a valid U.S. state name.');
      return;
    }

    const city = stateToCityMap[trimmedState];
    fetchWeather(city);
  };

  return (
    <div className={styles.weatherContainer}>
      <h2>Weather App</h2>
      <div className={styles.weatherSearch}>
        <input
          type="text"
          placeholder="Enter U.S. state name"
          value={state}
          onChange={(e) => setState(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className={styles.error}>{error}</p>}
      {weather && (
        <div className={styles.weatherDetails}>
          <p><strong>State:</strong> {state}</p>
          <p><strong>City:</strong> {weather.name}</p>
          <p><strong>Temperature:</strong> {Math.round(weather.main.temp - 273.15)}Â°C</p>
          <p><strong>Condition:</strong> {weather.weather[0].description}</p>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
